from scratchpad.pivot import PivotTableDefinition
from scratchpad.cache import PivotCacheDefinition
from scratchpad.records import PivotCacheRecords
from openpyxl.xml.functions import fromstring

# table
src = open("../pivot/pivotTable1.xml", "rb").read()
xml = fromstring(src)
pivot = PivotTableDefinition.from_tree(xml)

# cache
src = open("../pivot/pivotCacheDefinition1.xml", "rb").read()
xml = fromstring(src)
cache = PivotTableDefinition.from_tree(xml)

# records
src = open("../pivot/PivotCacheRecords1.xml", "rb").read()
xml = fromstring(src)
cache = PivotCacheRecords.from_tree(xml)
